const members = $input.all().map(item => {
  const member = item.json;

  // Parse phones (use different variable name to avoid conflict)
  const phonesData = member.phones;
  member.phones = [];
  if (phonesData && typeof phonesData === 'string') {
    const phoneEntries = phonesData.split('|');
    member.phones = phoneEntries.map(entry => {
      const [type, number] = entry.split(':');
      return { phone_type: type, phone_number: number, is_active: true };
    }).filter(p => p.phone_type && p.phone_number);
  }

  // Parse employment
  if (member.is_employed) {
    member.employment = {
      is_employed: member.is_employed,
      company_name: member.company_name,
      designation: member.designation,
      profession: member.profession,
      employment_start_date: member.employment_start_date,
      is_current: true
    };
  } else {
    member.employment = null;
  }

  // Parse family relationships
  member.relationships = [];
  if (member.family_members && typeof member.family_members === 'string') {
    const familyEntries = member.family_members.split('|');
    member.relationships = familyEntries.map(entry => {
      const [relationship, name] = entry.split(':');
      return { relationship_type: relationship, name };
    }).filter(r => r.relationship_type && r.name);
  }

  // Clean up temporary fields
  delete member.company_name;
  delete member.designation;
  delete member.profession;
  delete member.employment_start_date;
  delete member.family_members;

  return member;
});

return [{
  json: {
    success: true,
    members: members
  }
}];

const member = $input.first().json;

// Parse phone numbers if they exist
if (member.phones) {
  const phoneEntries = member.phones.split('|');
  member.phone_numbers = {};
  phoneEntries.forEach(entry => {
    const [type, number] = entry.split(':');
    if (type && number) {
      member.phone_numbers[type] = number;
    }
  });
  delete member.phones;
}

// Parse family members if they exist
if (member.family_members) {
  const familyEntries = member.family_members.split('|');
  member.family = familyEntries.map(entry => {
    const [relationship, name] = entry.split(':');
    return { relationship, name };
  }).filter(f => f.relationship && f.name);
  delete member.family_members;
}

// Format employment
if (member.is_employed) {
  member.employment = {
    is_employed: member.is_employed,
    company_name: member.company_name,
    designation: member.designation,
    profession: member.profession,
    employment_start_date: member.employment_start_date,
    is_current: true
  };
} else {
  member.employment = null;
}

// Clean up employment fields from main object
delete member.company_name;
delete member.designation;
delete member.profession;
delete member.employment_start_date;

return [{
  json: {
    success: true,
    member: member
  }
}];

