const output = [];

$input.all().forEach(item => {
  const data = item.json;
  const related = data.related_member;

  // Check if related_member exists
  if (!related) {
    console.log(`Skipping item - no related_member data`);
    return;
  }

  const email = related.email || "";

  if (email && email.trim() !== '') {
    output.push({
      json: {
        member_id: data.member_id,
        relationship_type: data.relationship_type,
        title: related.title || '',
        first_name: related.first_name || '',
        last_name: related.last_name || '',
        dob: related.dob || null,
        email: email.trim(),
        family_status: related.family_status || '',
        church_joining_date: related.church_joining_date || null,
        phones: related.phones || [],
        relationships: related.relationships || []
      }
    });
  } else {
    console.log(`Skipping ${related.first_name} ${related.last_name} - no valid email`);
  }
});

return output;

const memberData = $input.first().json;
const commands = [];

if (memberData.profile_pic) {
  const filename = memberData.profile_pic.replace('/uploads/', '');
  commands.push({
    json: {
      command: 'rm -f',
      file: `/data/uploads/${filename}`,
      type: 'profile_pic'
    }
  });
}

if (memberData.family_photo) {
  const filename = memberData.family_photo.replace('/uploads/', '');
  commands.push({
    json: {
      command: 'rm -f', 
      file: `/data/uploads/${filename}`,
      type: 'family_photo'
    }
  });
}

return commands.length ? commands : [{ json: { no_files: true } }];

const imageItems = $input.all(); // Gets array of all input items
const oldMemberData = $('Old Member Data').item.json; // Old member data from previous node

const filesToDelete = [];

// Process each image item
imageItems.forEach(item => {
  const imageData = item.json;

  // Delete old profile pic if we just saved a new one
  if (imageData.type === 'profile_pic' && oldMemberData.profile_pic) {
    const oldFilename = oldMemberData.profile_pic.replace('/uploads/', '');
    filesToDelete.push({
      json: {
        command: 'rm -f',
        file: `/data/uploads/${oldFilename}`,
        type: 'cleanup_old_profile'
      }
    });
  }

  // Delete old family photo if we just saved a new one
  if (imageData.type === 'family_photo' && oldMemberData.family_photo) {
    const oldFilename = oldMemberData.family_photo.replace('/uploads/', '');
    filesToDelete.push({
      json: {
        command: 'rm -f',
        file: `/data/uploads/${oldFilename}`,
        type: 'cleanup_old_family'
      }
    });
  }
});

return filesToDelete.length ? filesToDelete : [{ json: { no_cleanup_needed: true } }];

const memberIds = $input.all().map(item => item.json); // Get all member IDs and emails
const bulkData = $('Action Router1').first().json.body.data.members;
const results = [];

memberIds.forEach(memberInfo => {
  // Find the member data by email
  const member = bulkData.find(m => m.email === memberInfo.email);

  if (member) {
    const phones = [
      { type: 'primary', number: member.primary_phone },
      { type: 'whatsapp', number: member.whatsapp_phone },
      { type: 'emergency', number: member.emergency_phone },
      { type: 'origin', number: member.origin_phone }
    ];

    phones.forEach(phone => {
      if (phone.number) {
        results.push({
          json: {
            member_id: memberInfo.id,
            phone_type: phone.type,
            phone_number: phone.number.toString(),
            is_active: true
          }
        });
      }
    });
  }
});

return results.length ? results : [{ json: { no_phone_data: true } }];

const memberIds = $input.all().map(item => item.json); // [{id: 107, email: "..."}, ...]
const bulkMembers = $('Process Bulk Members1').all().map(item => item.json); // Individual member items
const results = [];

memberIds.forEach(memberInfo => {
  // Find the member data by email
  const member = bulkMembers.find(m => m.email === memberInfo.email);

  if (member) {
    // Process profile picture URL
    if (member.profile_pic && member.profile_pic.includes('drive.google.com')) {
      results.push({
        json: {
          type: 'profile_pic',
          member_id: memberInfo.id,
          drive_url: member.profile_pic,
          filename: `profile_${memberInfo.id}_${Date.now()}.jpg`
        }
      });
    }

    // Process family photo URL
    if (member.family_photo && member.family_photo.includes('drive.google.com')) {
      results.push({
        json: {
          type: 'family_photo',
          member_id: memberInfo.id,
          drive_url: member.family_photo,
          filename: `family_${memberInfo.id}_${Date.now()}.jpg`
        }
      });
    }
  }
});

return results.length ? results : [{ json: { no_images: true } }];
